import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import Button from "react-bootstrap/Button";

function App() {

  const [url, setUrl] = useState("https://raw.githubusercontent.com/Akhilesh-Gogikar/English-Puranas/master/puranas_jsons/1.1.1.json")
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // pause audio sound
  const goNext = () => {
    var new_url = "https://raw.githubusercontent.com/Akhilesh-Gogikar/English-Puranas/master/puranas_jsons/"+items["next"]+".json"
    setUrl(new_url)
  }

  // stop audio sound
  const goPrev = () => {
    var new_url = "https://raw.githubusercontent.com/Akhilesh-Gogikar/English-Puranas/master/puranas_jsons/"+items["prev"]+".json"
    setUrl(new_url)
    console.log(new_url)
    useEffect(() => {
      fetch(url)
        .then( res => res.json())
        .then(
          (result) => {
            console.log(result)
            setIsLoaded(true);
            setItems(result);
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        )
    }, [])
  }

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch(url)
      .then( res => res.json())
      .then(
        (result) => {
          console.log(result)
          setIsLoaded(true);
          setItems(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div className='App' >
    <header className="AppHeader">Error: {error.message}
    </header>
    </div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div className='App' >
      <header className="App-header">
      <p className='red-text-shadow'>{items.title}</p>
      <p className='red-text-shadow'>{items.text}</p>

      <div className="Music">

    <input type="button" className="btn btn-primary mr-2" value="Prev" onClick={goPrev}></input>

      <input type="button" className="btn btn-danger mr-2" value="Next" onClick={goNext}></input>

    </div>

      </header>
      </div>
    );
  }
}

export default App;
