import { SET_PAGE, RESET_STATE, UPDATE_SESSION } from "../actionTypes";

const initialState = {
  currentPage: '1',
  userID: null,
  session: []
};

export default function(state = initialState, action) {

  switch (action.type) {
    case SET_PAGE: {
      const { content } = action.payload;
        console.log(action.payload);
      return {
        ...state,
        currentPage:content,
        session: [...state.session, content]
      };
    }
    case UPDATE_USERID: {
      const { user } = action.payload;
      return {
        ...state,
        currentPage:user,
      };
    }
    case UPDATE_SESSION: {
      const { page } = action.payload;
      return {
        ...state,
        session: [...state.session, page]
      };
    }
    case RESET_STATE: {
      const { page } = action.payload;
      return {
        ...state,
        currentPage: '1',
        session: []
      };
    }
    default:
      return state;
  }
}
