import logo from './logo.svg';
import './App.css';
import React from 'react';
 import textData from "./dataClean.json";

function sectionText(props){

   let talkText = props.text;
   let periods = talkText.match(/\./g,' ').length;

   let indices = [];

  for (var v = 0; v < talkText.length; v++) {
     if(talkText[v] == "."){
         indices.push(v);
       }
     }

  let index1 = Math.round(periods/8);
  let index11 = indices[index1];

  let index2 = index1 * 2;
  let index22 = indices[index2];

  let index3 = index1 * 3;
  let index33 = indices[index3];

  let index4 = index1 * 4;
  let index44 = indices[index4];

  let index5 = index1 * 5;
  let index55 = indices[index5];

  let index6 = index1 * 6;
  let index66 = indices[index6];

  let index7 = index1 * 7;
  let index77 = indices[index7];

  let paragraph8End = talkText.length -1;

  let str1 = talkText.substring(0, index11);

  let str2 = talkText.substring(index11, index22);

  let str3 = talkText.substring(index22, index33);

  let str4 = talkText.substring(index33, index44);

  let str5 = talkText.substring(index44, index55);

  let str6 = talkText.substring(index55, index66);

  let str7 = talkText.substring(index66, index77);

  let str8 = talkText.substring(index77, paragraph8End);

    return( <div class="textContainer" >
       <p class="red-text-shadow" >${str1}</p>
       <p class="red-text-shadow" >${str2}</p>
       <p class="red-text-shadow" >${str3}</p>
       <p class="red-text-shadow" >${str4}</p>
       <p class="red-text-shadow" >${str5}</p>
       <p class="red-text-shadow" >${str6}</p>
       <p class="red-text-shadow" >${str7}</p>
       <p class="red-text-shadow" >${str8}</p>
   </div>);
}


function App() {
  return (
    <div className="App" >
      <header className="App-header">
      <p class="red-text-shadow">        18.22  </p>
        <p class="red-text-shadow">Tale of Bhadrasheel Brahmin</p>
      <img src={textData[18][22]['image']} />
      <sectionText text={textData[18][22]['text']}/>
        <p class="red-text-shadow">
        {textData[18][22]['text']}
        </p>

      </header>
    </div>
  );
}

export default App;
