import '../App.css';
import React, { useState, useEffect } from 'react';
import Button from "react-bootstrap/Button";
import { connect } from "react-redux";
import { setPage, updateSession } from "../redux/actions";
import { useHistory } from "react-router-dom";
import { getPage } from "../redux/selectors";
import GoogleAd from './googleAds';
import Navbar from "react-bootstrap/Navbar";
import logo from '../logo.png';
import database from "./firebaseDB"



import {
  FacebookShareCount
} from "react-share";

import {
  FacebookIcon,
  LinkedinIcon,
  TwitterIcon,
  WhatsappIcon
} from "react-share";

import {
  FacebookShareButton,
  LinkedinShareButton,
  TwitterShareButton,
  WhatsappShareButton
} from "react-share";

const AsyncImage = (props) => {
    const [loadedSrc, setLoadedSrc] = React.useState(null);
    React.useEffect(() => {
        setLoadedSrc(null);
        if (props.src) {
            const handleLoad = () => {
                setLoadedSrc(props.src);
            };
            const image = new Image();
            image.addEventListener('load', handleLoad);
            image.src = props.src;
            return () => {
                image.removeEventListener('load', handleLoad);
            };
        }
    }, [props.src]);
    if (loadedSrc === props.src) {
        return (
            <img {...props} className="img-fluid"/>
        );
    }
    return null;
};

function Puranas({propOne, setPage}) {

  const history = useHistory();

    const [userdata, setUserData] = useState([]);
    const [karma, setKarma] = useState(0);

  console.log(userdata)

  useEffect(() => {
    if (propOne.userId === ""){
      history.push('login');
    }
  }, [])

  useEffect(() => {
  if (propOne.userId != "" && userdata.length == 0){
    console.log("Creating a new user in rtdb")

      const userRef = database.ref('users')

      userRef.orderByChild("uid").equalTo(propOne.userId.uid).once("value",snapshot => {
      if (snapshot.exists()){
        const userData = snapshot.val();
        console.log("exists!");
        for (let id in userData){
          console.log(userData[id])
        }

      } else {

        const data = {
          displayName: propOne.userId.displayName,
          email: propOne.userId.email,
          photoURL: propOne.userId.photoURL,
          uid: propOne.userId.uid,
          phoneNumber: propOne.userId.phoneNumber,
          karma:0
        }
        userRef.push(data)
      }
  });

  }
}, [userdata, propOne])

