import Button from "react-bootstrap/Button";
// import Text from 'react-text';
import logo from '../logo.png';
import Main from "./main.js"
import { useHistory } from "react-router-dom";
import '../App.css';
import Navbar from "react-bootstrap/Navbar";

function About() {
  const history = useHistory();

  const goLogin = () => {
    history.push('/');
  }
  return (
    <div className="App">
    <Navbar bg="warning" variant="light">
    <Navbar.Brand href="/" >
    <img
      alt=""
      src={logo}
      width="35"
      height="35"
      className="d-inline-block align-top"
    />{' '}
      Simple Puranas
    </Navbar.Brand>
    <Navbar.Toggle />
  <Navbar.Collapse className="justify-content-end">
  <Navbar.Brand type="button" className="btn btn-link" style={{color:"white"}}>
  <a href="about" style={{color:"Brown"}}>About</a>
  </Navbar.Brand>
  <Navbar.Text>
&nbsp;&nbsp;
  </Navbar.Text>
    <Navbar.Text>
    <Button variant="outline-light" href="/login">Signin</Button>
    </Navbar.Text>
  </Navbar.Collapse>
  </Navbar>
    <header className="App-header">
      <p className="mb-0 h1" style={{textDecoration:"underline"}}>Simple Puranas</p>
      <br/>
      <p style={{textDecoration:"underline"}}>Goal of the Simple Puranas</p>
      <p>The ancient puranas contain stories written as verses in the times of sages</p>
      <p style={{textDecoration:"underline"}}>Source of simple puranas</p>
      <p style={{textDecoration:"underline"}}>Karma point system</p>
      <p style={{textDecoration:"underline"}}>How to use</p>

      <p>Indian puranas are stories explaining the fundamentals and origins of Indian hindu religion.</p>
      <p>Read and share stories about Indian gods, thier significance,</p>
      <p>ways to worship them, and thier endeavours to enhance humanity.</p>
      <p>Understand the meaning of the puranas and apply to enhance your lives.</p>
      <input type="button" className="btn btn-primary" value="Home" onClick={goLogin}></input>
    </header>
    </div>
  );
}


export default About;
